INSERT INTO `config_info` VALUES (15, 'snowy-gateway-app-dev.yaml', 'dev', '# 服务配置\nserver:\n  port: 9003\n\n# spring相关配置\nspring:\n  datasource:\n    driver-class-name: com.mysql.cj.jdbc.Driver\n    url: jdbc:mysql://localhost:3306/snowy-cloud?useUnicode=true&characterEncoding=utf-8&useSSL=false&allowPublicKeyRetrieval=true&nullCatalogMeansCurrent=true&useInformationSchema=true\n    username: root\n    password: 123456\n\n# Oracle数据库\n#spring:\n#  datasource:\n#    driver-class-name: oracle.jdbc.OracleDriver\n#    url: jdbc:oracle:thin:@localhost:1521:xe\n#    username: SNOWY-CLOUD-ORACLE\n#    password: 123456\n\n# SQLServer配置\n#spring:\n#  datasource:\n#    driverClassName: com.microsoft.sqlserver.jdbc.SQLServerDriver\n#    url: jdbc:sqlserver://localhost:1433;DatabaseName=snowy-cloud-mssql\n#    username: sa\n#    password: 123456\n\n# PostgreSQL配置\n#spring:\n#  datasource:\n#    driverClassName: org.postgresql.Driver\n#    url: jdbc:postgresql://127.0.0.1:5432/snowy-cloud-postgresql\n#    username: postgres\n#    password: 123456\n\n# 达梦数据库配置\n#spring:\n#  datasource:\n#    driver-class-name: dm.jdbc.driver.DmDriver\n#    url: jdbc:dm://localhost:5236/SYSDBA\n#    username: SYSDBA\n#    password: 123456789\n\n# 人大金仓数据库\n#spring:\n#  datasource:\n#    driver-class-name: com.kingbase8.Driver\n#    url: jdbc:kingbase8://localhost:54321/snowy-cloud-kingbase\n#    username: system\n#    password: 123456\n# #人大金仓数据库兼容问题，不需要加在sql语句中加public的解决方法：\n# #在根目录data下的kingbase.conf文档里面找到search_path = \'\"$user\",PUBLIC,sys_catalog\'进行替换放开\n# #重启数据库即可完全兼容，注意 sql中不能出现mysql中的关键字的单引号\n\n  # redis相关配置\n  redis:\n    database: 1\n    host: 127.0.0.1\n    port: 6379\n    password:\n    timeout: 10s\n    lettuce:\n      pool:\n        max-active: 200\n        max-wait: -1ms\n        max-idle: 10\n        min-idle: 0\n\n  boot:\n    admin:\n      client:\n        url: http://localhost:9001\n        instance:\n          # 默认使用的是主机名注册，改为使用ip注册\n          prefer-ip: true\n  cloud:\n    loadbalancer:\n      ribbon:\n        enabled: false\n      cache:\n        enabled: true\n        caffeine:\n          spec: initialCapacity=500,expireAfterWrite=5s\n    # nacos配置\n    nacos:\n      discovery:\n        server-addr: ${spring.cloud.nacos.config.server-addr}\n        namespace: ${spring.cloud.nacos.config.namespace}\n        group: ${spring.cloud.nacos.config.group}\n        username: ${spring.cloud.nacos.config.username}\n        password: ${spring.cloud.nacos.config.password}\n    sentinel:\n      transport:\n        dashboard: localhost:9002\n        port: 8719\n      scg:\n        fallback:\n          mode: response\n          response-status: 455\n          response-body: error!\n    # gateway配置\n    gateway:\n      default-filters:\n        - DedupeResponseHeader=Access-Control-Allow-Origin\n      globalcors:\n        add-to-simple-url-handler-mapping: true\n        corsConfigurations:\n          \'[/**]\':\n            allowed-origins-patterns: \"*\"\n            allowed-methods: \"*\"\n            allowed-headers: \"*\"\n            allow-credentials: true\n      discovery:\n        locator:\n          lowerCaseServiceId: true\n          enabled: true\n      routes:\n        - id: snowy-web-app\n          uri: lb://snowy-web-app\n          predicates:\n            - Path=/api/webapp/**\n          filters:\n            - SwaggerHeaderFilter\n            - StripPrefix=2 #去掉Path中前两个节点\n            - name: Retry\n              args:\n                retries: 1 #重试次数\n        - id: snowy-web-app\n          uri: lb://snowy-web-app\n          predicates:\n            - Path=/druid/**\n          filters:\n            - SwaggerHeaderFilter\n            - name: Retry\n              args:\n                retries: 1 #重试次数\n        - id: snowy-biz-app\n          uri: lb://snowy-biz-app\n          predicates:\n            - Path=/api/bizapp/**\n          filters:\n            - SwaggerHeaderFilter\n            - StripPrefix=2 #去掉Path中前两个节点\n            - name: Retry\n              args:\n                retries: 1 #重试次数\n\n# 开放所有endpoint，实际生产根据自身需要开放，出于安全考虑不建议全部开放\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \"*\"\n    health:\n      show-details: \"ALWAYS\"\n\n#########################################\n# sa-token configuration\n#########################################\nsa-token:\n  token-name: token\n  timeout: 2592000\n  activity-timeout: -1\n  is-concurrent: true\n  is-share: false\n  max-login-count: -1\n  token-style: random-32\n  is-log: false\n  is-print: false\n\n  # sa-token alone-redis configuration\n  alone-redis:\n    database: 2\n    host: ${spring.redis.host}\n    port: ${spring.redis.port}\n    password: ${spring.redis.password}\n    timeout: ${spring.redis.timeout}\n    lettuce:\n      pool:\n        max-active: ${spring.redis.lettuce.pool.max-active}\n        max-wait: ${spring.redis.lettuce.pool.max-wait}\n        max-idle: ${spring.redis.lettuce.pool.max-idle}\n        min-idle: ${spring.redis.lettuce.pool.min-idle}\n\n# knife4j 文档聚合\nknife4j:\n  enable: true\n  production: false\n  basic:\n    enable: true\n    username: admin\n    password: 123456\n  setting:\n    enableOpenApi: false\n    enableSwaggerModels: false\n    enableFooter: false\n    enableFooterCustom: true\n    footerCustomContent: Apache License 2.0 | Copyright 2022-[SNOWY](https://www.xiaonuo.vip)\n  enableAggregation: true\n  cloud:\n    enable: true\n    routes:\n      - name: snowy-web-app\n        location: /api/webapp/v2/api-docs?group=snowy-web-app\n        uri: localhost:9003\n      - name: snowy-biz-app\n        location: /api/bizapp/v2/api-docs?group=snowy-biz-app\n        uri: localhost:9003', 'bc46398fe9140ad87628ab2b462adb49', '2023-06-10 16:49:01', '2023-08-02 16:02:41', 'nacos', '192.168.0.105', '', 'snowy-cloud-2x-dev', '', '', '', 'yaml', '', '');
