om.mysql.cj.jdbc.Driver\n          url: jdbc:mysql://localhost:3306/snowy-cloud?useUnicode=true&characterEncoding=utf-8&useSSL=false&allowPublicKeyRetrieval=true&nullCatalogMeansCurrent=true&useInformationSchema=true\n          username: root\n          password: 123456\n      strict: true\n\n  # oracle\n#  datasource:\n#    dynamic:\n#      datasource:\n#        master:\n#          driver-class-name: oracle.jdbc.driver.OracleDriver\n#          url: jdbc:oracle:thin:@//127.0.0.1:1521/XE?remarksReporting=true\n#          username: SNOWY\n#          password: 12345678\n#      strict: true\n\n  # mssql\n#  datasource:\n#    dynamic:\n#      datasource:\n#        master:\n#          driver-class-name: com.microsoft.sqlserver.jdbc.SQLServerDriver\n#          url: jdbc:sqlserver://localhost:1433;DatabaseName=SNOWY\n#          username: sa\n#          password: 12345678\n#      strict: true\n\n  # dm database\n#  datasource:\n#    dynamic:\n#      datasource:\n#        master:\n#          driver-class-name: dm.jdbc.driver.DmDriver\n#          url: jdbc:dm://localhost:5236/SYSDBA\n#          username: SYSDBA\n#          password: SYSDBA\n#      strict: true\n\n  # kingbase database\n#  datasource:\n#    dynamic:\n#      datasource:\n#        master:\n#          driver-class-name: com.kingbase8.Driver\n#          url: jdbc:kingbase8://localhost:54321/snowy\n#          username: SYSTEM\n#          password: 123456\n#      strict: true\n\n      # druid global configuration\n      public-key: MFwwDQYJKoZIhvcNAQEBBQADSwAwSAJBAMWiTVtdXFVrgFHDDKELZM0SywkWY3KjugN90eY5Sogon1j8Y0ClPF7nx3FuE7pAeBKiv7ChIS0vvx/59WUpKmUCAwEAAQ==\n      druid:\n        initial-size: 5\n        max-active: 20\n        min-idle: 5\n        max-wait: 60000\n        pool-prepared-statements: true\n        max-pool-prepared-statement-per-connection-size: 20\n        validation-query-timeout: 2000\n        test-on-borrow: false\n        test-on-return: false\n        test-while-idle: true\n        time-between-eviction-runs-millis: 6000\n        min-evictable-idle-time-millis: 300000\n        filters: stat\n        break-after-acquire-failure: false\n\n    # druid monitor configuration\n    druid:\n      stat-view-servlet:\n        enabled: true\n        login-username: admin\n        login-password: 123456\n        allow: \"\"\n\n  #########################################\n  # jackson configuration\n  #########################################\n  jackson:\n    time-zone: GMT+8\n    date-format: yyyy-MM-dd HH:mm:ss\n    locale: zh_CN\n\n  #########################################\n  # redis configuration\n  #########################################\n  redis:\n    database: 1\n    host: 127.0.0.1\n    port: 6379\n    password:\n    timeout: 10s\n    lettuce:\n      pool:\n        max-active: 200\n        max-wait: -1ms\n        max-idle: 10\n        min-idle: 0\n\n#########################################\n# 开放所有endpoint，实际生产根据自身需要开放，出于安全考虑不建议全部开放\n#########################################\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \"*\"\n    health:\n      show-details: \"ALWAYS\"\n\n#########################################\n# mybatis-plus configuration\n#########################################\nmybatis-plus:\n  configuration:\n    log-impl: org.apache.ibatis.logging.stdout.StdOutImpl\n  global-config:\n    banner: false\n    enable-sql-runner: true\n    db-config:\n      id-type: ASSIGN_ID\n      logic-delete-field: DELETE_FLAG\n      logic-delete-value: DELETED\n      logic-not-delete-value: NOT_DELETE\n  mapper-locations: classpath*:vip/xiaonuo/**/mapping/*.xml,com/bstek/**/mapping/*.xml\n  type-handlers-package: vip.xiaonuo.common.handler\n\n#########################################\n# easy-trans configuration\n#########################################\neasy-trans:\n  is-enable-redis: true\n  is-enable-global: true\n  is-enable-tile: true\n\n#########################################\n# sa-token configuration\n############################